package au.com.lingvo.domain;import org.springframework.data.rest.core.annotation.RestResource;import javax.persistence.*;@Entity@Table(name = "language")@RestResourcepublic class InterestLanguage {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private long id;    private String name;    public long getId() {        return id;    }    public void setId(long id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }}