package au.com.lingvo.domain;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;@Entitypublic class Location {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private long id;    private String city;    private String country;    private Double lat;    private Double lng;    public String getCity() {        return city;    }    public void setCity(String city) {        this.city = city;    }    public String getCountry() {        return country;    }    public void setCountry(String country) {        this.country = country;    }    public Double getLat() {        return lat;    }    public void setLat(Double lat) {        this.lat = lat;    }    public Double getLng() {        return lng;    }    public void setLng(Double lng) {        this.lng = lng;    }}