package au.com.lingvo.domain;import javax.persistence.*;import java.util.Date;@Entitypublic class Message {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private long id;    private Date createOn = new Date();    private Long fromId;    private Long toId;    @Column(name = "text", length = 9000)    private String text;    public Date getCreateOn() {        return createOn;    }    public void setCreateOn(Date createOn) {        this.createOn = createOn;    }    public Long getFromId() {        return fromId;    }    public void setFromId(Long fromId) {        this.fromId = fromId;    }    public Long getToId() {        return toId;    }    public void setToId(Long toId) {        this.toId = toId;    }    public String getText() {        return text;    }    public void setText(String text) {        this.text = text;    }    public long getId() {        return id;    }    public void setId(long id) {        this.id = id;    }}