package au.com.lingvo.domain;import org.springframework.data.rest.core.annotation.RestResource;import javax.persistence.*;@Entity@RestResourcepublic class ProfileInterestLanguage {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private long id;    private String interestLanguage;    @Enumerated(EnumType.STRING)    private LanguageLevel languageLevel;    public long getId() {        return id;    }    public void setId(long id) {        this.id = id;    }    public LanguageLevel getLanguageLevel() {        return languageLevel;    }    public void setLanguageLevel(LanguageLevel languageLevel) {        this.languageLevel = languageLevel;    }    public String getInterestLanguage() {        return interestLanguage;    }    public void setInterestLanguage(String interestLanguage) {        this.interestLanguage = interestLanguage;    }}