package au.com.lingvo.domain;import org.springframework.data.rest.core.annotation.RestResource;import javax.persistence.*;@Entity@RestResourcepublic class ProfileInterestLanguage {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private long id;    @ManyToOne    @JoinColumn(name = "language_id")    private InterestLanguage interestLanguage;    @ManyToOne    @JoinColumn(name = "profile_id")    private Profile profile;    @Enumerated(EnumType.STRING)    private LanguageLevel languageLevel;    public InterestLanguage getInterestLanguage() {        return interestLanguage;    }    public void setInterestLanguage(InterestLanguage interestLanguage) {        this.interestLanguage = interestLanguage;    }    public long getId() {        return id;    }    public void setId(long id) {        this.id = id;    }    public LanguageLevel getLanguageLevel() {        return languageLevel;    }    public void setLanguageLevel(LanguageLevel languageLevel) {        this.languageLevel = languageLevel;    }    public Profile getProfile() {        return profile;    }    public void setProfile(Profile profile) {        this.profile = profile;    }}