package au.com.lingvo.domain;import org.springframework.data.rest.core.annotation.RestResource;import javax.persistence.*;@Entity@RestResourcepublic class ProfileKnownLanguage {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private long id;    private String name;    private String code;    @Enumerated(EnumType.STRING)    private LanguageLevel level;    public long getId() {        return id;    }    public void setId(long id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getCode() {        return code;    }    public void setCode(String code) {        this.code = code;    }    public LanguageLevel getLevel() {        return level;    }    public void setLevel(LanguageLevel level) {        this.level = level;    }}