package au.com.lingvo.domain;import javax.persistence.*;@Entitypublic class ProfileKnownLanguage {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private long id;    @ManyToOne    @JoinColumn(name = "language_id")    private KnownLanguage knownLanguage;    @Enumerated(EnumType.STRING)    private LanguageLevel languageLevel;    public long getId() {        return id;    }    public void setId(long id) {        this.id = id;    }    public LanguageLevel getLanguageLevel() {        return languageLevel;    }    public void setLanguageLevel(LanguageLevel languageLevel) {        this.languageLevel = languageLevel;    }    public KnownLanguage getKnownLanguage() {        return knownLanguage;    }    public void setKnownLanguage(KnownLanguage knownLanguage) {        this.knownLanguage = knownLanguage;    }}