package au.com.lingvo.domain;import javax.persistence.*;@Entitypublic class ProfileLanguage {    @ManyToOne    private Language language;    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private long id;    @Enumerated(EnumType.STRING)    private LanguageLevel languageLevel;    public Language getLanguage() {        return language;    }    public void setLanguage(Language language) {        this.language = language;    }    public long getId() {        return id;    }    public void setId(long id) {        this.id = id;    }    public LanguageLevel getLanguageLevel() {        return languageLevel;    }    public void setLanguageLevel(LanguageLevel languageLevel) {        this.languageLevel = languageLevel;    }}