package au.com.lingvo.repo;import au.com.lingvo.domain.ProfileInterestLanguage;import org.springframework.data.repository.PagingAndSortingRepository;import org.springframework.data.repository.query.Param;import org.springframework.data.rest.core.annotation.RepositoryRestResource;import java.util.List;@RepositoryRestResourcepublic interface ProfileInterestLanguageRepository extends PagingAndSortingRepository<ProfileInterestLanguage, Long> {    List<ProfileInterestLanguage> findByProfileId(@Param("profileId") Long profileId);}