package au.com.lingvo.repo;import au.com.lingvo.domain.Profile;import au.com.lingvo.projections.Interest;import org.springframework.data.repository.PagingAndSortingRepository;import org.springframework.data.repository.query.Param;import org.springframework.data.rest.core.annotation.RepositoryRestResource;import java.util.List;import java.util.Set;@RepositoryRestResource(collectionResourceRel = "profile", path = "profile", excerptProjection = Interest.class)public interface ProfileRepository extends PagingAndSortingRepository<Profile, Long> {    List<Profile> findByEmail(@Param("email") String email);    List<Profile> findByKnownIdIn(@Param("knownLanguageId") Set<Long> knownLanguageId);}