package au.com.lingvo.repo;import au.com.lingvo.domain.Profile;import org.springframework.data.repository.PagingAndSortingRepository;import org.springframework.data.repository.query.Param;import org.springframework.data.rest.core.annotation.RepositoryRestResource;import java.util.List;@RepositoryRestResource(collectionResourceRel = "profile", path = "profile")public interface ProfileRepository extends PagingAndSortingRepository<Profile, Long> {    List<Profile> findByName(@Param("name") String name);}