package au.com.lingvo.repo;import au.com.lingvo.domain.Profile;import org.springframework.data.repository.PagingAndSortingRepository;import org.springframework.data.repository.query.Param;import org.springframework.data.rest.core.annotation.RepositoryRestResource;import java.util.List;import java.util.Set;@RepositoryRestResource(collectionResourceRel = "profile", path = "profile")public interface ProfileRepository extends PagingAndSortingRepository<Profile, Long> {    List<Profile> findByKnownNameIn(@Param("knownLanguage") Set<String> knownLanguages);    List<Profile> findByEmail(@Param("email") String email);}