package au.com.lingvo.services;import au.com.lingvo.domain.Message;import au.com.lingvo.domain.Profile;import au.com.lingvo.repo.MessageRepository;import au.com.lingvo.repo.ProfileRepository;import com.fasterxml.jackson.databind.ObjectMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.scheduling.annotation.Scheduled;import org.springframework.stereotype.Service;import java.io.IOException;import java.net.URL;import java.util.logging.Logger;@Servicepublic class DummyData {    private Logger log = Logger.getLogger(DummyData.class.getName());    @Autowired    private ProfileRepository profileRepository;    @Autowired    private MessageRepository messageRepository;    @Scheduled(initialDelay = 1000, fixedDelay = 991000)    public void addData() {        ObjectMapper mapper = new ObjectMapper();        try {            URL resource = getClass().getClassLoader().getResource("profile1.json");            Profile profile = mapper.readValue(resource, Profile.class);            profileRepository.save(profile);            resource = getClass().getClassLoader().getResource("profile2.json");            profile = mapper.readValue(resource, Profile.class);            profileRepository.save(profile);            resource = getClass().getClassLoader().getResource("profile3.json");            profile = mapper.readValue(resource, Profile.class);            profileRepository.save(profile);            resource = getClass().getClassLoader().getResource("message1.json");            Message message = mapper.readValue(resource, Message.class);            messageRepository.save(message);            resource = getClass().getClassLoader().getResource("message2.json");            message = mapper.readValue(resource, Message.class);            messageRepository.save(message);            resource = getClass().getClassLoader().getResource("message3.json");            message = mapper.readValue(resource, Message.class);            messageRepository.save(message);        } catch (IOException e) {            e.printStackTrace();        }    }}